export const lodash = Lodash;

export function random(from, to, fixed) {
  return ((Math.random() * (to - from)) + from).toFixed(fixed) * 1;
}

export function ucFirst(str) {
  return str ? str.charAt(0).toUpperCase() + str.slice(1) : str;
}
export function capitalize(str) { return ucFirst(str); }

export function dateYYYYMMDD(date) {
  if (!date) { return undefined; }
  return moment(date).format('YYYY-MM-DD');
}

export function isArray(array) {
  return Array.isArray(array);
}

export function trim(elem) {
  if (elem.trim) {
    return elem.trim();
  }
  return null;
}

export async function timeout(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}
export const wait = timeout;

export function isObject(obj, force = true) {
  if (force) {
    return !isArray(obj) && isObject(obj, false);
  }
  return typeof obj === 'object';
}

export function toLowerCase(str) {
  return str.toLowerCase();
}

export function isEmpty(data) {
  if (data === null || data === undefined) {
    return true;
  }
  if (typeof data === 'string') {
    return data === '';
  } else if (Array.isArray(data)) {
    return data.length === 0;
  } else if (typeof data === 'object') {
    for (const key in data) {
      if (data.hasOwnProperty(key)) {
        return false;
      }
    }
    return true;
  }
  return null;
}

export function notEmpty(elem) {
  return !isEmpty(elem);
}

export function nullIfEmpty(elem) {
  //TODO test if object, string or array
  return isEmpty(elem) ? null : elem;
}

export function jsonEncode(json) {
  return JSON.stringify(json).catch(() => null);
}

export function jsonDecode(string) {
  return JSON.parse(string).catch(() => null);
}

export function toNumber(elem) {
  return Number(elem);
}

export function deepCopy(obj) {
  return Lodash.cloneDeep(obj);
}

export function objValToArray(obj) {
  return Object.keys(obj).map((k) => obj[k]);
}

import StatusCode from './StatusCode';

export function test(func, errMsg) {
  return (elem, route, { ctx, keyBody }) => {
    if (!func(elem)) {
      route.throw(StatusCode.badRequest, errMsg || `${ctx.state.__('invalide param:')} ${keyBody}`);
    }
    return elem;
  };
}

export function trasform(func) {
  return elem => func(elem);
}

export function regex(reg) {
  return (elem) => {
    if (reg instanceof RegExp) {
      return elem.match(reg);
    }
    return false;
  };
}

export function isDateBetween(d1, d2) {
  return (d) => {
    if (d1.getTime() <= d.getTime() && d2.getTime() >= d.getTime()) {
      return true;
    }
    if (d2.getTime() <= d.getTime() && d1.getTime() >= d.getTime()) {
      return true;
    }
    return false;
  };
}

# **KoaSmart** is a framework based on **Koajs2**, which allows you to develop RESTful APIs with : **Class**, **Decorator**, **Params checker**

[![Build Status](https://secure.travis-ci.org/ysocorp/koa-smart.png?branch=master "Test")](http://travis-ci.org/ysocorp/koa-smart)
[![NPM version](http://badge.fury.io/js/koa-smart.png)](https://npmjs.org/package/koa-smart "View this project on NPM")

A framework based on [Koajs2](https://github.com/koajs/koa) with **Decorator**, **Params checker** and a **base of modules** ([`cors`](https://www.npmjs.com/package/kcors), [`bodyparser`](https://github.com/koajs/bodyparser), [`compress`](https://github.com/koajs/compress), [`I18n`](https://github.com/koa-modules/i18n), etc... ) to allow you to develop a smart api easily 
```sh
  export default class RouteUsers extends Route {

    // get route: http://localhost:3000/users/get/:id
    @Route.Get({
      path: 'get/:id'
    })
    async get(ctx) {
      const user = await this.models.users.findById(ctx.params.id);
      this.assert(user, 404, ctx.state.__('User not found'));
      this.sendOk(ctx, user);
    }

    // post route: http://localhost:3000/users/add
    @Route.Post({
      accesses: [Route.accesses.public],
      params: { // params to allow: all other params will be rejected
        email: true, // return an 400 if the body doesn't contain email key
        name: false,
      },
    })
    async add(ctx) {
      const body = this.body(ctx); // or ctx.request.body
      // body can contain only an object with email and name field
      const user = await this.models.user.create(body);
      this.sendCreated(ctx, user);
    }

  }
```

